mapper.select:
select * from(select t.*,rownum rn from (select u.adminId,m.middleId,r.roleid,u.admin,r.rolename from admintb u,roleMiddleTb m,roleTb r where u.adminId=m.adminid and m.roleId = r.roleId and r.roleId=2 and u.stype=1
and u.admin like '%${admin}%'
) t where rownum <= #{page}*5) where rn > (#{page}-1)*5

	select * from(select t.*,rownum rn
    from
	(select * from usertb where deleteId = 1
	
	  and userName like '%${userName}%' 
     
     
	  and onStage = #{onStage}
      
      
	  and sex like '%${sex}%' 
     
    
	  and teleNum = #{teleNum}
      
       
	  and email like '%${email}%' 
     
		) t where rownum <= #{currentPage}*5) where rn > (#{currentPage}-1)*5

	select * from usertb where deleteId = 1
	 
	  and userName like '%${userName}%' 
     
     
	  and onStage = #{onStage}
      
      
	  and sex like '%${sex}%' 
     
     
	  and teleNum = #{teleNum}
      
       
	  and email like '%${email}%'
