mapper.select:
		select *from (select rownum rw ,comboTb.* from comboTb
		where
		deleteId!=16
		
			and name like #{name}
		
		
			and fare like #{price}
		
		
			and month like #{time}
		
		order by comboId desc) where rw >=#{forNum} and
		rw <=#{toNum}

select rownum rw ,comboTb.* from comboTb
where
deleteId!=16

	and name like #{name}


	and fare like #{price}


	and month like #{time}

order by comboId desc
